import min from 'lodash/min'

export default function (payload: {
    title: string
    blocks: {
        name: string
        text: string
        placeholder: string
        image: string
    }[]
    authors: { name: string; organization: string }[]
}) {
    const { title, blocks, authors } = payload

    const getAuthorsWithInstitutions = (authors: { name: string; organization: string }[]) => {
        return authors.map(
            (item, i) =>
                item.name + ' from ' + item.organization + (i < authors.length - 1 ? ', ' : '')
        )
    }

    const getQR = () => {
        return 'data:image/png;base64,'
    }

    const getLogo = () => {
        return 'data:image/png;base64,'
    }

    const getImageIcon = () => {
        return 'data:image/svg+xml;base64,'
    }

    /**
     * @deprecated
     * @returns {Promise<unknown>}
     */
    /* getDataUrl() {
            const [route] = this.$route.matched
            const png = 'santi.png'
            const url = route.name === 'download-id' ? '../' + png : png
            
            return new Promise((resolve, reject) => {
                const xhr = new XMLHttpRequest()
                xhr.open('GET', url, true)
                xhr.responseType = 'blob'
                xhr.onload = function (e) {

                    const reader = new FileReader()
                    reader.onload = function (event) {
                        const res = event.target.result
                        resolve(res)
                    }
                    reader.onerror = (e) => reject(e)
                    const file = this.response
                    reader.readAsDataURL(file)
                }
                xhr.onerror = (e) => reject(e)
                xhr.send()
            })
        }, */
    const useWatermarkFn = (useWatermark) => {
        if (useWatermark)
            return {
                text: 'PosterLab',
                color: '#1C2268',
                opacity: 0.07,
                bold: true,
                italics: false,
            }
        else return ''
    }

    const tableLayout = {
        hLineWidth(i, node) {
            return i === 0 || i === node.table.body.length ? 1 : 2
        },
        vLineWidth(i, node) {
            return i === 0 || i === node.table.widths.length ? 1 : 2
        },
        hLineColor(i, node) {
            return i === 0 || i === node.table.body.length ? 'gray' : '#42E6DF'
        },
        vLineColor(i, node) {
            return i === 0 || i === node.table.widths.length ? 'gray' : '#42E6DF'
        },
    }

    const capitalize = (text: string) => {
        if (text) {
            const nameArr = text.split('')
            nameArr[0] = nameArr[0].toUpperCase()
            return nameArr.join('')
        } else {
            return ''
        }
    }

    const getLandscapeCol = (name: string, text: string, index: number, image: string) => {
        return {
            width: 250,
            layout: tableLayout,
            table: {
                heights: 210,
                widths: [240],
                body: [
                    [
                        {
                            stack: [
                                {
                                    text: `${index || ''}. ${capitalize(name)}`,
                                    style: 'subheader',
                                },
                                image && {
                                    image: getImageIcon(),
                                    width: 15,
                                    margin: [215, -18, 0, 10],
                                    fillColor: 'gray',
                                    background: 'gray',
                                },
                                {
                                    margin: 10,
                                    text,
                                    style: 'content',
                                },
                            ],
                        },
                    ],
                ],
            },
        }
    }

    const getPortraitCol = (name: string, text: string, index: number, image: string) => {
        return {
            layout: tableLayout,
            table: {
                heights: 220,
                widths: [245],
                body: [
                    [
                        {
                            stack: [
                                {
                                    text: `${index || ''}. ${capitalize(name)}`,
                                    style: 'subheader',
                                },
                                image && {
                                    image: getImageIcon(),
                                    width: 15,
                                    margin: [215, -18, 0, 10],
                                    fillColor: 'gray',
                                    background: 'gray',
                                },
                                {
                                    margin: 10,
                                    text,
                                    style: 'content',
                                },
                            ],
                        },
                    ],
                ],
            },
        }
    }

    const addRows = (rows, page) => {
        for (let i = 0; i < rows.length; i++) {
            // We will only add rows with 1 or 2 elements
            if (rows[i].length === 0) break

            if (i === 0) {
                page.content.splice(i + 1, 0, {
                    columns: rows[i],
                })
            } else {
                page.content.splice(i + 1, 0, {
                    margin: [0, 15, 0, 0],
                    columns: rows[i],
                })
            }
        }

        return page
    }

    const getPdfLandscapeDefinition = (useWatermark) => {
        const cols = blocks
            .filter((block) => block.name !== 'title')
            .map(({ name, text, image }, index) => getLandscapeCol(name, text, index + 1, image))

        // Create rows with 2 columns for Portrait and 3 for Landscape
        const rows: Array<any> = []
        for (let i = 0, start = 0; i < 2; start += 3, i++) {
            rows[i] = cols.slice(start, min([cols.length, start + 3]))
        }

        const page = {
            pageSize: 'A4',
            pageOrientation: 'landscape',
            pageMargins: [30, 30, 30, 5], // right, top, left, bottom
            watermark: useWatermarkFn(useWatermark),
            content: [
                {
                    columns: [
                        {
                            style: 'header',
                            width: 500,
                            text: title,
                        },
                        {
                            style: 'affiliation',
                            width: 'auto',
                            text: getAuthorsWithInstitutions(payload.authors),
                        },
                        // Used in case of a University demo
                        // {
                        //   stack: [
                        //     {
                        //       image: await this.getDataUrl(),
                        //       width: 50,
                        //       margin: [0, 0, 20, 0] // left, top, right, bottom
                        //     }
                        //   ]
                        // }
                    ],
                },
                {
                    columns: [
                        {
                            stack: [
                                {
                                    // auto-sized columns have their widths based on their content
                                    width: 40,
                                    text: 'CREATED IN ONE CLICK BY',
                                    color: '#1C2268',
                                    fontSize: 5,
                                    margin: [0, 0, 0, 0], // left, top, right, bottom
                                    absolutePosition: {
                                        x: 30,
                                        y: 550,
                                    },
                                },
                                {
                                    image: getLogo(),
                                    width: 80,
                                    margin: [0, 0, 0, 0], // left, top, right, bottom
                                    absolutePosition: {
                                        x: 30,
                                        y: 560,
                                    },
                                },
                            ],
                            absolutePosition: {
                                // x: 100,
                                // y: 555,
                            },
                        },
                        {
                            // auto-sized columns have their widths based on their content
                            width: 100,
                            text: 'canvas.posterlab.co',
                            color: '#1C2268',
                            fontSize: 9,
                            margin: [0, 0, 0, 0], // left, top, right, bottom
                            absolutePosition: {
                                x: 680,
                                y: 555,
                            },
                        },
                        {
                            stack: [
                                {
                                    image: getQR(),
                                    width: 30,
                                    margin: [0, 0, 0, 0], // left, top, right, bottom
                                    absolutePosition: {
                                        x: 780,
                                        y: 546,
                                    },
                                },
                            ],
                        },
                    ],
                },
            ],
            styles: {
                header: {
                    fontSize: 24,
                    bold: true,
                    margin: [0, 0, 0, 30],
                },
                subheader: {
                    fontSize: 16,
                    bold: true,
                    margin: [10, 10, 10, 0],
                },
                affiliation: {
                    fontSize: 12,
                    margin: [0, 0, 0, 20],
                },
                content: {
                    fontSize: 8,
                },
            },
            defaultStyle: {
                columnGap: 15,
            },
        }

        return addRows(rows, page)
    }

    const getPdfPortraitDefinition = (useWatermark) => {
        const cols = blocks
            .filter((block) => block.name !== 'title')
            .map(({ name, text, image }, index) => getPortraitCol(name, text, index + 1, image))

        const rows: Array<any> = []
        for (let i = 0, start = 0; i < 3; start += 2, i++) {
            rows[i] = cols.slice(start, min([cols.length, start + 2]))
        }

        const page = {
            pageSize: 'A4',
            pageOrientation: 'portrait',
            pageMargins: [35, 30, 35, 5], // right, top, left, bottom
            watermark: useWatermarkFn(useWatermark),
            content: [
                {
                    columns: [
                        {
                            style: 'header',
                            width: 260,
                            text: title,
                        },
                        {
                            style: 'affiliation',
                            width: 'auto',
                            text: getAuthorsWithInstitutions(authors),
                        },
                        // Used in case of an University demo
                        // {
                        //   stack: [
                        //     {
                        //       image: await this.getDataUrl(),
                        //       width: 50,
                        //       margin: [0, 0, 0, 0] // left, top, right, bottom
                        //     }
                        //   ]
                        // }
                    ],
                },
                {
                    columns: [
                        {
                            // auto-sized columns have their widths based on their content
                            width: 60,
                            text: 'THIS SCIENTIFIC\n CANVAS WAS CREATED\n IN ONE CLICK BY',
                            color: '#1C2268',
                            fontSize: 5,
                            margin: [0, 0, 0, 0], // left, top, right, bottom
                            absolutePosition: {
                                x: 35,
                                y: 810,
                            },
                        },
                        {
                            stack: [
                                {
                                    image: getLogo(),
                                    width: 40,
                                    margin: [0, 0, 0, 0], // left, top, right, bottom
                                },
                            ],
                            absolutePosition: {
                                x: 105,
                                y: 813,
                            },
                        },
                        {
                            // auto-sized columns have their widths based on their content
                            width: 100,
                            text: 'canvas.posterlab.co',
                            color: '#1C2268',
                            fontSize: 9,
                            margin: [0, 0, 0, 0], // left, top, right, bottom
                            absolutePosition: {
                                x: 435,
                                y: 815,
                            },
                        },
                        {
                            stack: [
                                {
                                    image: getQR(),
                                    width: 30,
                                    margin: [0, 0, 0, 0], // left, top, right, bottom
                                    absolutePosition: {
                                        x: 530,
                                        y: 805,
                                    },
                                },
                            ],
                        },
                    ],
                },
            ],
            styles: {
                header: {
                    fontSize: 24,
                    bold: true,
                    margin: [0, 0, 40, 20],
                },
                subheader: {
                    fontSize: 16,
                    bold: true,
                    margin: [10, 10, 10, 0],
                },
                affiliation: {
                    fontSize: 12,
                    margin: [0, 0, 0, 20],
                },
                content: {
                    fontSize: 10,
                },
            },
            defaultStyle: {
                columnGap: 15,
            },
        }

        return addRows(rows, page)
    }

    return { getPdfLandscapeDefinition, getPdfPortraitDefinition }
}
